package edu.matc.MultiThreadingHalloween;import org.apache.log4j.Logger;import java.util.Date;/** Created 10/03/17 * *  @author nfenev */class Kid implements Runnable {    private final Logger logger = Logger.getLogger(this.getClass());    private String name;    private Date inTime;    private Halloween halloween;    Kid (Halloween halloweenIn) {        logger.info("Created a new kid");        this.halloween = halloweenIn;    }    String getName() {        return name;    }    Date getInTime() {        return inTime;    }    void setName(String nameIn) {        this.name = nameIn;        logger.info("A name has been set for a kid");    }    void setInTime(Date inTimeIn) {        this.inTime = inTimeIn;        logger.info("A time has been set for a kid");    }    public void run()    {        goGetCandy();    }    private synchronized void goGetCandy()    {        halloween.add(this);    }}