package edu.matc.MultiThreadingHalloween;import org.apache.log4j.Logger;/** Created 10/03/17 * *  @author nfenev */public class RileyActions {    private final Logger logger = Logger.getLogger(this.getClass());    final void allThreads() {        logger.info("Starting a new RileyActions instance");        Halloween halloween = new Halloween();        Riley riley = new Riley(halloween);        KidGenerator kidGenerator = new KidGenerator(halloween);        Thread threadRiley = new Thread(riley);        Thread threadKidGenerator = new Thread(kidGenerator);        threadKidGenerator.start();        logger.info("This class instance (RileyActions) started a kid generator thread");        threadRiley.start();        logger.info("This class instance (RileyActions) started a Riley thread");    }}